import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';

function DescriptionObituary ()
{
  const { id } = useParams();

  const [ obituary, setObituary ] = useState( [] );

  useEffect( () =>
  {
    axios.get( 'http://localhost:8081/read/' + id )
      .then( res =>
      {
        console.log( res )
        setObituary( res.data[ 0 ] );
      } )
      .catch( err => console.log( err ) );
  }, [] );

  return (
    <div className='d-flex justify-content-center align-items-center' style={ { background: '#F2F2F8' } }>
      <div className='w-50 rounded p-3'>
        <h2>Obituary Details</h2>

        { obituary.length > 0 && (
          <>
            <div className='p-2'>
              <h3>{ obituary.ID }</h3>
              <h3>{ obituary.title }</h3>
              <h3>{ obituary.dob }</h3>
              <h3>{ obituary.dod }</h3>
              <h3>{ obituary.time }</h3>
              <h3>{ obituary.description }</h3>
              <h3>{ obituary.country }</h3>
              <h3>{ obituary.city }</h3>
              <h3>{ obituary.religion }</h3>
            </div>

            <button className='btn ms-2' style={ { background: '#326346', color: '#ffff' } }>Edit</button>
          </> ) }
      </div>
    </div>
  )
}

export default DescriptionObituary
